CREATE TABLE users(userID INT NOT NULL AUTO_INCREMENT, username VARCHAR(255) NOT NULL, password VARCHAR(255) NOT NULL, privateKey VARCHAR(4096) NOT NULL, publicKey VARCHAR(4096) NOT NULL, PRIMARY KEY (userID));
CREATE TABLE contacts(userIDa INT NOT NULL, userIDb INT NOT NULL, PRIMARY KEY (userIDa, userIDb));
CREATE TABLE messages(messageID INT NOT NULL AUTO_INCREMENT, contactID INT NOT NULL, userIDa INT NOT NULL, userIDb INT NOT NULL, message TEXT(65525) NOT NULL, PRIMARY KEY (messageID), FOREIGN KEY (userIDa, userIDb) REFERENCES contacts(userIDa, userIDb));